    private void showTranslate() {
        EditText nome = (EditText)findViewById(R.id.transalte_name);
        EditText language = (EditText)findViewById(R.id.transalte_languege);
        if(!nome.getText().toString().equals("Ex: Igor Sanches")
                &&  nome != null
                && !nome.getText().toString().trim().equals("")){
            nome.setTextColor(getColor(android.R.color.holo_green_dark));
            if(!language.getText().toString().equals("English")
                    &&  language != null
                    && !language.getText().toString().trim().equals("")){
                language.setTextColor(getColor(android.R.color.holo_green_dark));

                createFiles(nome.getText().toString(), language.getText().toString());

            }else{
                language.setTextColor(getColor(android.R.color.holo_red_dark));
                Toast.makeText(this, "Error", Toast.LENGTH_SHORT).show();
                dialog.dismiss();
            }
        }else{
            nome.setTextColor(getColor(android.R.color.holo_red_dark));
            Toast.makeText(this, "Error", Toast.LENGTH_SHORT).show();
            dialog.dismiss();
        }
    }

    private void createFiles(String name, String language) {
        String info = "Nome: " + name + "\n" +
                "Idioma: " + language + "_" + Locale.getDefault().getLanguage() + "-" + Locale.getDefault().getCountry() + "\n" +
                "Aparelho: " + Build.DEVICE + "\n" +
                "Modelo: " + Build.MODEL + "\n" +
                "Fabricante: " + Build.MANUFACTURER + "\n" +
                "Marca: " + Build.BRAND + "\n" +
                "Nmero da verso: " + Build.DISPLAY + "\n" +
                "SDk: " + Build.VERSION.SDK + "\n" +
                "Android: " + Build.VERSION.RELEASE + "\n" +
                "Data e hora: " + DateTime.Now(this).toString("dd/MM/yyyy HH:mm:ss");


        try{

            //Caminho que ser zipado
            File isZipar = StorageFile.getData();
            if(isZipar != null){
                //Criar o arquivo .zip
                File zipFile = StorageFile.getZipData();

                //Criar o arquivo .txt
                File txtFile = StorageFile.getInfoData();
                if(txtFile != null){
                    if(txtFile.canWrite()) {
                        BufferedWriter writer = new BufferedWriter(new FileWriter(txtFile));
                        writer.write(info);
                        writer.close();
                    }
                }

                //Criar o arquivo .xml
                File xmlFile = StorageFile.getXmlData();
                if(xmlFile != null){
                    if(xmlFile.canWrite()) {
                        BufferedWriter writer = new BufferedWriter(new FileWriter(xmlFile));
                        writer.write(xml());
                        writer.close();
                    }
                }
                zipFolder(isZipar.getAbsolutePath(), zipFile.getAbsolutePath());
                isZipar.delete();
                dialog.dismiss();
                senEmail(zipFile);
            }

        }catch (Exception x) {
            dialog.dismiss();
            Toast.makeText(this, x.getMessage(), Toast.LENGTH_SHORT).show();
        }
    }

    private void senEmail(File zip) {
        try{
            Uri rui = StorageFile.getFileShare(this, zip);
            Intent i = new Intent(Intent.ACTION_SEND);
            i.setType("text/plain");
            i.putExtra(Intent.EXTRA_SUBJECT, DateTime.Now(this).toString());
            i.putExtra(Intent.EXTRA_STREAM, rui);
            i.setData(Uri.parse("mailto:perfectscan_feedback@outlook.com"));
            i.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
            startActivity(Intent.createChooser(i, getString(R.string.share)));
        }catch (ActivityNotFoundException x){
            Toast.makeText(this, getString(R.string.error_send), Toast.LENGTH_SHORT).show();
        }
    }

    static public void zipFolder(String srcFolder, String destZipFile) throws Exception {
        ZipOutputStream zip = null;
        FileOutputStream fileWriter = null;

        fileWriter = new FileOutputStream(destZipFile);
        zip = new ZipOutputStream(fileWriter);

        addFolderToZip("", srcFolder, zip);
        zip.flush();
        zip.close();
    }

    static private void addFileToZip(String path, String srcFile, ZipOutputStream zip)
            throws Exception {

        File folder = new File(srcFile);
        if (folder.isDirectory()) {
            addFolderToZip(path, srcFile, zip);
        } else {
            byte[] buf = new byte[1024];
            int len;
            FileInputStream in = new FileInputStream(srcFile);
            zip.putNextEntry(new ZipEntry(path + "/" + folder.getName()));
            while ((len = in.read(buf)) > 0) {
                zip.write(buf, 0, len);
            }
        }
    }

    static private void addFolderToZip(String path, String srcFolder, ZipOutputStream zip)
            throws Exception {
        File folder = new File(srcFolder);

        for (String fileName : folder.list()) {
            if (path.equals("")) {
                addFileToZip(folder.getName(), srcFolder + "/" + fileName, zip);
            } else {
                addFileToZip(path + "/" + folder.getName(), srcFolder + "/" + fileName, zip);
            }
        }
    }

